update.foreach:
UPDATE MEMBER_DETAIL SET MEMBER_NAME = #{member.memberName}, MEMBER_GN = #{member.memberGn}, MEMBER_DEPT = #{member.memberDept}, MEMBER_RO = #{member.memberRo}, MEMBER_POS = #{member.memberPos},
MEMBER_STDAY = #{member.memberStDay}, MEMBER_ST = #{member.memberSt}
WHERE MEMBER_ID = #{member.memberId}

